add_action('wp_head','hook_woometa');

function hook_woometa() {
	global $product;
  
	$output = "";
	if(is_product()){
		$output .="<!-- This part is generated by a snippet you can find on http://vandersluijs.nl -->\n";
		$output .= (null !== $product->get_regular_price()) ? "<meta property='product:original_price:amount' content='{$product->get_regular_price()}' />\n": "" ;
		$output .= "<meta property='product:original_price:currency' content='{get_woocommerce_currency()}' />\n";
	

		$output .= (null !== $product->get_price_excluding_tax()) ? "<meta property='product:pretax_price:amount' content='{$product->get_price_excluding_tax()}' />\n" : "" ;
		$output .= "<meta property='product:pretax_price:currency' content='{get_woocommerce_currency()}' />\n";
	
		$output .= (null !== $product->get_price()) ? "<meta property='product:price:amount'content='{$product->get_price()}' />\n" :"" ;
		$output .= "<meta property='product:price:currency'content='{get_woocommerce_currency()}' />\n";

		$output .= ($product->has_weight()) ? "<meta property='product:weight:value'content='{$product->get_weight()}' />\n" : "" ;
	
		if($product->is_on_sale()){
			$output .= (null !== $product->get_sale_price()) ? "<meta property='product:sale_price:amount' content='{$product->get_sale_price()}' />\n" : "" ;
			$output .= "<meta property='product:sale_price:currency' content='{}' />\n";
		  	$output .= "<meta property='product:sale_price:currency' content='{}' />\n";
		}
	  	$output .="<!-- End of snippet by http://vandersluijs.nl -->\n";
	}

  echo $output;
}
